import io
import os
import shutil
source_root = "C:/Users/lws/gits/pcgen/docs/"
root = "G:/renamed/"

for old_name, new_name in files:
    old_path = os.path.join(source_root, old_name)

    new_dir, _ = os.path.split(new_name)
    new_dir = os.path.join(root, new_dir)
    new_path = os.path.join(root, new_name)

    if not os.path.exists(new_dir):
        os.makedirs(new_dir)

    with io.open(old_path, mode="r", encoding="utf-8") as in_file:
        html = in_file.read()

    for find, replace in files:
        html = html.replace(find, replace)

    with io.open(new_path, mode="w", encoding="utf-8") as out_file:
        out_file.write(html)
